sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var today = new GlideDateTime().getDate();
		var inputdate = new GlideDateTime(strDate).getDate();

		// If the compareDate is before today, return true, else return false
		if (inputdate.before(today)) {
			return true;
		}
		else {
			return false;
		}
	},
isDateToday: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var today = new GlideDateTime().getDate();
		var inputdate = new GlideDateTime(strDate).getDate();

		if(today.compareTo(inputdate) == 0){
			return true;}
		else
		    {return false;}

},
    type: 'NeedItUtils'
};
